mapper.select:
select
count(1)
from t_c_trade_insure ti LEFT JOIN t_c_trade_assured ta ON ti.trade_id = ta.trade_id AND ta.flag = 1
where status_id != 0

    and ti.trade_id = #{tradeId,jdbcType=INTEGER}


    and ti.policy_number = #{policyNumber,jdbcType=INTEGER}


    and ti.status_id = #{statusId,jdbcType=INTEGER}


    and ti.create_time >= #{createTime,jdbcType=TIMESTAMP}


    and ti.create_time <= #{createTime1,jdbcType=TIMESTAMP}


    and ti.recognizee_name like CONCAT('%',#{recognizeeName},'%')


    and ta.real_name like CONCAT('%',#{realName},'%')


    and ti.product_name like CONCAT('%',#{productName},'%')


    and ti.source_id=#{sourceId}


    and ti.member_id = #{memberId,jdbcType=INTEGER}


    and ti.insure_begin_date = #{insureBeginDate,jdbcType=TIMESTAMP}

select
ti.order_id,ti.status_id,ti.insurance_num,ti.create_time,ti.modified_time,ti.member_id,ti.product_name,ti.fid,
ti.insure_type,ti.insure_years,ti.market_amount,ti.buy_number,ti.pay_years_type,ti.pay_years,
ti.insure_begin_date,ti.insure_end_date,ti.total_amount,ti.source_id,ti.insure_file_url,ti.recognizee_name,ti.policy_number,ti.trade_id,ta.real_name
from t_c_trade_insure ti LEFT JOIN t_c_trade_assured ta ON ti.trade_id = ta.trade_id AND ta.flag = 1
where ti.status_id != 0

    and ti.trade_id = #{tradeId,jdbcType=INTEGER}


    and ti.policy_number = #{policyNumber,jdbcType=INTEGER}


    and ti.status_id = #{statusId,jdbcType=INTEGER}


    and ti.create_time >= #{createTime,jdbcType=TIMESTAMP}


    and ti.create_time <= #{createTime1,jdbcType=TIMESTAMP}


    and ti.recognizee_name like CONCAT('%',#{recognizeeName},'%')


    and ta.real_name like CONCAT('%',#{realName},'%')


    and ti.product_name like CONCAT('%',#{productName},'%')


    and ti.source_id=#{sourceId}


    and ti.member_id = #{memberId,jdbcType=INTEGER}


    and ti.insure_begin_date = #{insureBeginDate,jdbcType=TIMESTAMP}

order by ti.create_time desc
limit #{pageFirstItem},#{pageSize}
